mapper.select:
SELECT 

FROM tb_likes a

WHERE a.id = #{id}

	AND a.user_id = #{holder.id}


	AND a.news_id = #{newsId}

SELECT 

FROM tb_likes a

WHERE 
	a.user_id = #{holder.id}
	AND 
	a.news_id = #{newsId}

SELECT 

FROM tb_likes a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{holder.id}




		ORDER BY ${page.orderBy}

SELECT 

FROM tb_likes a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{holder.id}




		ORDER BY ${page.orderBy}

SELECT 

FROM tb_likes a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
DELETE FROM tb_likes
WHERE
	user_id = #{holder.id}

		AND news_id = #{newsId}

UPDATE tb_likes SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
